--
-- Структура таблицы `[PREFIX]training_lessons`
--

CREATE TABLE `[PREFIX]training_lessons` (
  `lesson_id` int(11) NOT NULL,
  `training_id` int(11) NOT NULL,
  `section_id` int(11) NOT NULL DEFAULT '0',
  `block_id` int(11) NOT NULL,
  `name` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `alias` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL,
  `cover` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `img_alt` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `status` tinyint(4) NOT NULL,
  `sort` smallint(6) NOT NULL,
  `title` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `less_desc` text COLLATE utf8mb4_unicode_ci,
  `attach` longtext COLLATE utf8mb4_unicode_ci COMMENT 'вложение и его заголовок',
  `hits` int(11) NOT NULL DEFAULT '0',
  `meta_desc` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `meta_keys` varchar(255) COLLATE utf8mb4_unicode_ci DEFAULT NULL,
  `auto_access_lesson` tinyint(4) DEFAULT '0' COMMENT '0 - выводим кнопку для отметки прохождения, 1 - Автоматическое прохождение, когда вошел в урок',
  `access_type` tinyint(4) NOT NULL,
  `access_groups` text COLLATE utf8mb4_unicode_ci,
  `access_planes` text COLLATE utf8mb4_unicode_ci,
  `access_hidden` tinyint(1) NOT NULL DEFAULT '2' COMMENT 'Скрывать урок, если нет доступа. 1-да, 2-нет. По умолчанию используется 2(то есть уроки не скрываются)',
  `access_time_type` tinyint(4) NOT NULL DEFAULT '0' COMMENT 'доступ по времени (от начала прохождения курса, от даты регистрации, от конкретной даты, из настроек курса)',
  `access_time_value` int(11) DEFAULT NULL COMMENT 'время открытия',
  `duration` int(11) DEFAULT NULL COMMENT 'продолжительность',
  `public_date` int(11) DEFAULT NULL,
  `end_date` int(11) DEFAULT NULL,
  `show_hits` tinyint(4) NOT NULL DEFAULT '0',
  `show_comments` tinyint(4) DEFAULT '0',
  `rating` float DEFAULT NULL,
  `by_button` mediumtext COLLATE utf8mb4_unicode_ci COMMENT 'кнопка купить',
  `shedule` tinyint(1) DEFAULT '1' COMMENT 'Открытие урока 1 - сразу, 2 - с задержкой',
  `shedule_hidden` tinyint(1) NOT NULL DEFAULT '2' COMMENT 'Скрывать урок, если не подошла дата открытия. 1-да, 2-нет. По умолчанию используется 2(то есть уроки не скрываются)',
  `shedule_type` tinyint(1) DEFAULT NULL COMMENT 'Тип открытия 1 - относительная дата, 2 - конкретная дата',
  `shedule_relatively` tinyint(1) DEFAULT NULL COMMENT 'Относительно чего открыть, 1 - Входа в предыдущий урок, 2 - Входа в первый урок, 3 - Начало тренинга',
  `shedule_open_date` int(11) DEFAULT NULL COMMENT 'Дата открытия, если конкретная дата',
  `shedule_how_fast_open` tinyint(1) DEFAULT NULL COMMENT 'Как быстро открыть доступ 1- На следующий день, 2- через Х дней, 3 - Дождаться дня недели',
  `shedule_count_days` smallint(6) DEFAULT NULL COMMENT 'Количество дней, если выбрано через Х дней',
  `shedule_access_time_weekday` tinyint(1) DEFAULT NULL COMMENT 'День недели 1 - ПН,2 - ВТ, 3 - СР, 4 - ЧТ, 5 - ПТ, 6 - СБ, 7 - ВС',
  `create_date` int(11) NOT NULL,
  `shedule_relatively_specific_lesson` int(11) DEFAULT NULL COMMENT 'ИД урока относительно которого открыть урок'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

